choose.otherwise:
SELECT ab.approval_box_id, ab.approval_box_name,approval_box_used_status
FROM approval_box ab
JOIN company c ON ab.comp_id = c.comp_id
WHERE ab.box_delete_status != 1
AND ab.comp_id = #{company}
ORDER BY ab.sort_order ASC;

mapper.select:
select e.est_id from establishment  e
join organization_user ou on e.est_name = ou.est_name
where ou.org_user_id =#{orgUserId};

SELECT * FROM ( SELECT DISTINCT ab.approval_box_id, ab.approval_box_name, ab.sort_order FROM approval_box ab JOIN organization_user ou ON ou.org_user_id = #{orgUserId} LEFT JOIN box_use_department bud ON bud.approval_box_id = ab.approval_box_id LEFT JOIN company c ON (bud.use_id = c.comp_id AND bud.using_category = 'C') LEFT JOIN department d ON (bud.use_id = d.dept_id AND bud.using_category = 'D') LEFT JOIN user u ON (bud.use_id = ou.org_user_id AND bud.using_category = 'U') WHERE ab.box_delete_status != 1 AND ab.approval_box_used_status != 0 AND ( (ab.menu_using_range = 'T' AND ab.comp_id = #{compId}) OR c.comp_name = ou.comp_name OR d.dept_id = ou.dept_id OR u.user_id = ou.org_user_id )
UNION
SELECT ab.approval_box_id, ab.approval_box_name, ab.sort_order FROM approval_box ab WHERE ab.menu_using_range = 'T' AND ab.comp_id = 1 AND ab.box_delete_status != 1 AND ab.approval_box_used_status != 0 ) AS combined_results ORDER BY sort_order ASC;

SELECT ab.approval_box_id as boxId, ab.approval_box_name as boxName, cc.code_id, cc.code_value FROM common_code cc JOIN approval_box_view_item abvi ON (abvi.code_id = cc.code_id AND cc.section_id = '004') JOIN approval_box ab ON ab.approval_box_id = abvi.approval_box_id WHERE ab.menu_using_range = 'T' AND ab.comp_id = 1 AND ab.box_delete_status != 1 AND ab.approval_box_used_status != 0
UNION
SELECT DISTINCT ab.approval_box_id as boxId, ab.approval_box_name as boxName, cc.code_id, cc.code_value FROM common_code cc JOIN approval_box_view_item abvi ON (abvi.code_id = cc.code_id AND cc.section_id = '004') JOIN approval_box ab ON ab.approval_box_id = abvi.approval_box_id LEFT JOIN box_use_department bud ON bud.approval_box_id = ab.approval_box_id LEFT JOIN company c ON bud.use_id = c.comp_id AND bud.using_category = 'C' LEFT JOIN department d ON bud.use_id = d.dept_id AND bud.using_category = 'D' LEFT JOIN organization_user ou ON bud.use_id = ou.org_user_id AND bud.using_category = 'U' WHERE ab.box_delete_status != 1 AND ab.approval_box_used_status != 0 AND ( (ab.menu_using_range = 'T' AND ab.comp_id = #{compId}) OR c.comp_id = #{compId} OR d.dept_id = #{deptId} OR ou.org_user_id = #{orgUserId} );
