mapper.select:
select product_id, product_full_name
from product_number
where seq_code = #{seqCode};

mapper.insert:
insert into product_log (log_id, log_date, product_id)
values((select distinct product_num from product_number t1
        left join
            product_log t2 on t1.product_id = t2.product_id
        where seq_code = #{seqCode} and product_full_name = #{productFullName}),
now(),
#{productId}
);
